i.d:
 var l=Player.commentList.length;
 for (i=0;i<l;i++){
   var cmt=Player.commentList[i];
   if (cmt.mode<7 & cmt.time>1710) 
cmt.fontSize=20;
cmt.txt="
"+cmt.txt;
cmt.color=0xff99ff;  
 }

function textAndSakura(Char,Xposition,Yposition,Vapptime,Vstaytime,Vdistime,Vsize)

{



	for (var i = 1;i<7;i++){


				var Vsizestar=Utils.rand(15,25);
				var Vfalltime=Utils.rand(2,3.5);
				var Vfall=Utils.rand(70,150);
				var random1=Utils.rand(-Vsize,Vsize);
				var c = $.createCanvas({
							motionGroup:[

								{x:{fromValue:Xposition,lifeTime:Vapptime+Vstaytime},y:{fromValue:Yposition+20,lifeTime:Vapptime+Vstaytime},alpha:{fromValue:0,toValue:0, lifeTime:Vapptime+Vstaytime}},
								{alpha:{fromValue:1,toValue:1,lifeTime:Vdistime+Vfalltime},x:{fromValue:Xposition+2*random1,lifeTime:Vdistime+Vfalltime},y:{fromValue:Yposition+20,toValue:Yposition+Vfall,lifeTime:Vdistime+Vfalltime},rotationZ:{fromValue:0,toValue:720,lifeTime:Vdistime+Vfalltime}}],
							lifeTime:Vapptime+Vstaytime+Vdistime+Vfalltime
							}
							);






				cmt_2 =$.createComment('＊',{
 							motionGroup:[
								{alpha:{fromValue:0,toValue:0, lifeTime:Vapptime+Vstaytime}},
								{alpha:{fromValue:0,toValue:1, lifeTime:Vdistime}},
								{alpha:{fromValue:1,toValue:0, lifeTime:Vfalltime}}
 								],
								lifeTime: Vapptime+Vstaytime+Vdistime+Vfalltime,
 				       			x:0,
                                       			y:0,
                                       			color:0xffffff,
							fontsize:Vsizestar,
							parent:c

 			  			      }
						  );


				setFilter(cmt_2);

				}				 

			var cmt_1 = $.createComment(Char,{
 						motionGroup:[
								{y:{fromValue:Yposition+10,toValue:Yposition,lifeTime:Vapptime},alpha:{fromValue:0,toValue:1,lifeTime:Vapptime}},
								{alpha:{fromValue:1,toValue:1,lifeTime:Vstaytime}},
							    	{y:{fromValue:Yposition,toValue:Yposition+20,lifeTime:Vdistime},alpha:{fromValue:1,toValue:0,lifeTime:Vdistime}}
							     ],
						x:Xposition,
						y:Yposition,
						lifeTime: Vapptime+Vstaytime+Vdistime,
 						color:0xffffff,
						fontsize:Vsize

 					      }
					  );

			setFilter(cmt_1);
}

function setFilter(cmt)
{
	var gf1=$.createGlowFilter(0xff66ff,1,10,10,2,1,false,false);
	var cmtf = cmt.getTextFormat(0,1);cmtf.bold=false;cmt.setTextFormat(cmtf,0,1);cmt.filters=[gf1];
}


function sakuraFall(String,Xposition,Yposition,Vdistance,Vapptime,Vstaytime,Vdistime,Vspeed,Vsize)
{
var Str = String;
Len = Str.length;
var i = 0;
interval(function(){
i++;

Sub = Str.substring(i-1,i);

textAndSakura(Sub,Xposition,Yposition,Vapptime,Vstaytime,Vdistime,Vsize);


Xposition = Xposition + Vsize/Vdistance;
},Vspeed,Len);

}


