								{alpha:{fromValue:1,toValue:1,lifeTime:Vstaytime}},
							    	{y:{fromValue:Yposition,toValue:Yposition+20,lifeTime:Vdistime},alpha:{fromValue:1,toValue:0,lifeTime:Vdistime}}
							     ],
						x:Xposition,
						y:Yposition,
						lifeTime: Vapptime+Vstaytime+Vdistime,
 						color:0xffffff,
						fontsize:Vsize

 					      }
					  );

			setFilter(cmt_1);
}

function setFilter(cmt)
{
	var gf1=$.createGlowFilter(0xff66ff,1,10,10,2,1,false,false);
	var cmtf = cmt.getTextFormat(0,1);cmtf.bold=false;cmt.setTextFormat(cmtf,0,1);cmt.filters=[gf1];
}


